    }

    const cartItems=<ul className={styles['cart-items']}>
        {cartCtx.items.map(item => <CartItem key={item.key} name={item.name} amount={item.amount} price={item.price} onRemove={cartItemRemoveHandler} onAdd={cartItemAddHandler}/>)}</ul>

    return(
    <Modal hideCart={hideCart}>
        {cartItems}
       <div className={styles.total}>
           <span>Total Amount</span>
           <span>{totalAmount}</span>
       </div>
        <div className={styles.actions}>
            <button className={styles['button--alt']} onClick={hideCart}>Close</button>
            {hasItems&&<button className={styles.button}>Order</button>}
        </div>
    </Modal>);
}

export default Cart;
