import React, {useRef, useState} from 'react';
import styles from "./Checkout.module.css";

type CheckoutProps = {
    onCancel: () => void;
}

type formInputsValidityType = {
    name: boolean,
    street: boolean,
    city: boolean,
    postalCode: boolean,
}

//validation logic
const isEmpty = (param:string) => {
    return param.trim().length === 0;
}

const isFiveChars = (param:string) => {
    return param.trim().length === 5;
}

const Checkout = ({onCancel} : CheckoutProps) => {
    const [formInputsValidity, setFormInputsValidity] = useState<formInputsValidityType>({
        name: true,
        street: true,
        city: true,
        postalCode: true,
    })

    const nameInputRef = useRef<HTMLInputElement>(null);
    const streetInputRef = useRef<HTMLInputElement>(null);
    const postalCodeInputRef = useRef<HTMLInputElement>(null);
    const cityInputRef = useRef<HTMLInputElement>(null);
    const confirmHandler = (event: React.FormEvent<HTMLFormElement>) => {
        event.preventDefault();

        const enteredName = nameInputRef.current?.value;
        const enteredStreet = streetInputRef.current?.value;
        const enteredPostalCode = postalCodeInputRef.current?.value;
        const enteredCity = cityInputRef.current?.value;

        const enteredNameIsValid = !isEmpty(enteredName!);
        const enteredStreetIsValid = !isEmpty(enteredStreet!);
        const enteredCityIsValid = !isEmpty(enteredCity!);
        const enteredPostalCodeIsValid = isFiveChars(enteredPostalCode!);

        setFormInputsValidity(
            {
                name: enteredNameIsValid,
                street: enteredStreetIsValid,
                city: enteredCityIsValid,
                postalCode: enteredPostalCodeIsValid,
            }
        );

        const formIsValid =
            enteredNameIsValid
            && enteredStreetIsValid
            && enteredCityIsValid
            && enteredPostalCodeIsValid;

        if(!formIsValid){
            return;
        }


    };

    return (
        <form className={styles.form} onSubmit={confirmHandler}>
            <div className={styles.control}>
                <label htmlFor='name'>Your Name</label>
                <input type='text' id='name' ref={nameInputRef}></input>
                {!formInputsValidity && <p>Please enter a valid name!</p>}
            </div>
            <div className={styles.control}>
                <label htmlFor='street'>Street</label>
                <input type='text' id='street' ref={streetInputRef}></input>
            </div>
            <div className={styles.control}>
                <label htmlFor='postal'>Postal Code</label>
                <input type='text' id='postal' ref={postalCodeInputRef}></input>
            </div>
            <div className={styles.control}>
                <label htmlFor='city'>City</label>
                <input type='text' id='city' ref={cityInputRef}></input>
            </div>
            <div className={styles.actions}>
                <button type="button" onClick={onCancel}>Cancel</button>
                <button className={styles.submit}>Confirm</button>
            </div>
        </form>
    ) ;
}

export default Checkout;
