import React, {useContext} from 'react';
import styles from "./HeaderCartButton.module.css";
import CartIcon from "../Cart/CartIcon";
import {itemType} from "../../store/CartProvider";
import CartContext from "../../store/cart-context";

type HeaderCartButtonProps = {
    showCart: () => void
}
const HeaderCartButton = ({showCart} : HeaderCartButtonProps) => {
    const cartCtx = useContext(CartContext);
    const numberOfItems:number = cartCtx.items.reduce((currNumber:number, item: itemType) =>
       currNumber + item.amount, 0);

    const btnClasses = ``;

    return <button className={styles.button} onClick={showCart}>
            <span className={styles.icon}>
                <CartIcon/>
            </span>
            <span>
                Your Cart
            </span>
            <span className={styles.badge}>
                {numberOfItems}
            </span>
        </button>
}

export default HeaderCartButton;
