import React from "react";
import styles from "./Input.module.css"

type InputProps =  {
    input:{id:string,
        type: string,
        min: string,
        max: string
        step: string
        defaultValue:string,},
    label:string,
}
const Input = React.forwardRef( ({input, label}:InputProps) => {
    return <div className={styles.input}>
        <label htmlFor={input.id}>{label}</label>
        <input {...input}/>
    </div>
});

export default Input;
